mapper.insert:
    INSERT INTO 
	    	members(  email 
	  				, password 
	  				, name
	  				, gender
	  				, pw_question
	  				, pw_answer )
    VALUES( #{email} 
            , #{password}
            , #{name}
            , #{gender}
            , #{pwQuestion}
            , #{pwAnswer} )

mapper.select:

SELECT 
	password
FROM
 	members
 WHERE
 	email=#{email} 
 	AND
 	pw_question=#{pwQuestion}
 	AND
 	pw_answer=#{pwAnswer}
